from web3 import Web3
import time


def _deadline():
    '''
        Get a predefined deadline. 10min by default (same as the Uniswap SDK)
    '''
    return int(time.time()) + 30 * 60


def _multiplyBasedOnDecimals(amount, decimals):
    ''' 
        Get the token value in smart contract native amount
    '''
    return float(amount)*(10**decimals)

def _connectToWeb3(http_prov="https://bsc-dataseed.binance.org/"):
    '''
        Connect to any web3 provide *Binance *QuickNode *Localhost (FOR LOCALNODE) *ETC..
    '''
    return Web3(Web3.HTTPProvider(http_prov))

# Check if TXN is sucessful and proceed accoridngly 
def _checkTxnSuccess(web3, tx_hex, c_addr, c_deci,c_price, amnt, b_ord, my_addr, p_key):
    # Check if buy or sell order
    if b_ord:
        # Check if transaction was a sucess
        if web3.eth.get_transaction_receipt(tx_hex)['status']:
            # update database with the hex value of txn
            # _d_updateTransactions(tx_hex)
            # add to query for sell txns to execute after 1 block confirmation
            # _q_addtoSellTxns(c_addr, amnt)  
            print('Okay Great bought %s\n'%str(amnt))
            print('txn_hash: %s'%str(tx_hex))
            return True
        else:
            pass
    else:
        # Check if transaction was a sucess
        if web3.eth.get_transaction_receipt(tx_hex)['status']:
            # sell the rest of token since slippage is set @ standard 15%
            print('Okay Great Sold %s\n'%str(amnt))
            print('txn_hash: %s'%str(tx_hex))
            
            # update database with the hex value of txn
            # _d_updateTransactions(tx_hex)
            # add to finished txns to display on UI
            # _q_addtoFinishedTxns(c_addr, amnt)
            return True
        else:
            print('Didnt sell trying again..')
            # create another sell order or make sure to sell via UIUX
            _createSellOrder(web3,c_addr,c_deci,c_price,my_addr,p_key)

# Check if approve went through
def _checkTxnApprove(web3, tx_hex):
    try:
        if web3.eth.get_transaction_receipt(tx_hex)['status']:
            return True
        raise ValueError('No Gas..')
    except:
        return False

